select
id,user_id,team_id,customer_category_id,product_project_id,
goal_month,amount_s,amount_t,amount_y,amount_c,
created_at,updated_at
from buss_result_goal
where 1 = 1

    and user_id = #{user_id}


    and team_id = #{team_id}


    and customer_category_id = #{customer_category_id}


    and product_project_id = #{product_project_id}


    and goal_month = #{goal_month}


    and amount_s = #{amount_s}


    and amount_t = #{amount_t}


    and amount_y = #{amount_y}


    and amount_c = #{amount_c}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


limit 1

select
id,user_id,team_id,customer_category_id,product_project_id,
goal_month,amount_s,amount_t,amount_y,amount_c,
created_at,updated_at
from buss_result_goal
where 1 = 1

    and user_id = #{user_id}


    and team_id = #{team_id}


    and customer_category_id = #{customer_category_id}


    and product_project_id = #{product_project_id}


    and goal_month = #{goal_month}


    and amount_s = #{amount_s}


    and amount_t = #{amount_t}


    and amount_y = #{amount_y}


    and amount_c = #{amount_c}


    and created_at = #{created_at}


    and updated_at = #{updated_at}

select sum(amount_s+amount_t+amount_y+amount_c)
from buss_result_goal
where 1 = 1

    and user_id = #{user_id}


    and team_id = #{team_id}


    and customer_category_id = #{customer_category_id}


    and product_project_id = #{product_project_id}


    and goal_month = #{goal_month}


    and amount_s = #{amount_s}


    and amount_t = #{amount_t}


    and amount_y = #{amount_y}


    and amount_c = #{amount_c}


    and created_at = #{created_at}


    and updated_at = #{updated_at}

select a.id,a.user_id,a.team_id,a.customer_category_id,a.product_project_id, a.goal_month,a.amount_s,a.amount_t,a.amount_y,a.amount_c, b.product_brand_id from buss_result_goal a left join product_project b on b.id = a.product_project_id where goal_month = #{goal_month}
