mapper.select:
select
    id,name,parent_id,created_at,updated_at
    from customer_category
where delete_status = 0

    and name = #{name}


    and parent_id = #{parent_id}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


limit 1

