mapper.select:
select
id,customer_shop_id,customer_name,auth_type,platform,
expire_time,brand_id,brand_name,link,notify_user,
has_read,created_at,updated_at
from notify_customer_auth
where 1 = 1

    and customer_shop_id = #{customer_shop_id}


    and customer_name = #{customer_name}


    and auth_type = #{auth_type}


    and platform = #{platform}


    and expire_time = #{expire_time}


    and brand_id = #{brand_id}


    and brand_name = #{brand_name}


    and link = #{link}


    and notify_user = #{notify_user}


    and has_read = #{has_read}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


limit 1

select
id,customer_shop_id,customer_name,auth_type,platform,
expire_time,brand_id,brand_name,link,notify_user,
has_read,created_at,updated_at
from notify_customer_auth
where 1 = 1

    and customer_shop_id = #{customer_shop_id}


    and customer_name = #{customer_name}


    and auth_type = #{auth_type}


    and platform = #{platform}


    and expire_time = #{expire_time}


    and brand_id = #{brand_id}


    and brand_name = #{brand_name}


    and link = #{link}


    and notify_user = #{notify_user}


    and has_read = #{has_read}


    and created_at = #{created_at}


    and updated_at = #{updated_at}
