mapper.select:
select
id,name,created_at,updated_at
from product_brand
where delete_status = 0

    and id = #{id}


    and name = #{name}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


    and deleted_at = #{deleted_at}


    and delete_status = #{delete_status}


limit 1

select
id,name,created_at,updated_at
from product_brand
where delete_status = 0

    and name = #{name}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


    and deleted_at = #{deleted_at}


    and delete_status = #{delete_status}

where delete_status = 0
