mapper.select:
select
id,name,barcode,product_brand_id,product_project_id,
created_at,updated_at
from product
where delete_status = 0

    and id = #{id}


    and name = #{name}


    and barcode = #{barcode}


    and product_brand_id = #{product_brand_id}


    and product_project_id = #{product_project_id}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


    and deleted_at = #{deleted_at}


    and delete_status = #{delete_status}


limit 1

select
id,name,barcode,product_brand_id,product_project_id,
created_at,updated_at
from product
where delete_status = 0

    and name = #{name}


    and barcode = #{barcode}


    and product_brand_id = #{product_brand_id}


    and product_project_id = #{product_project_id}


    and created_at = #{created_at}


    and updated_at = #{updated_at}


    and deleted_at = #{deleted_at}


    and delete_status = #{delete_status}

select a.id,a.name,a.barcode, a.product_brand_id,b.name as product_brand_name, a.product_project_id,c.name as product_project_name, c.is_major,a.created_at from product a left join product_brand b on b.id = product_brand_id left join product_project c on c.id = product_project_id where a.delete_status = 0
and a.name like concat('%',#{name},'%')
and a.product_brand_id = #{product_brand_id}
and a.product_project_id = #{product_project_id}
order by a.created_at desc limit #{offset},#{limit}

select count(1)
from product a
where a.delete_status = 0

    and a.name like concat('%',#{name},'%')


    and a.product_brand_id = #{product_brand_id}


    and a.product_project_id = #{product_project_id}
